import logo from './logo.svg';
import './App.css';
import { useCallback, useState } from 'react';
import UploadCloudinary from './cloudinary/UploadCloudinary';


function App() {
  const [uploadLoading, setUploadLoading] = useState(false);
  const [image, setImage] = useState(null);
  const [alertMessage, setAlertMessage] = useState({
    type: '',
    message: ''
  });
  const [formState, setFormState] = useState({
    email: '',
    phoneNumber: '',
  });

  const handleFile = async e => {
    setUploadLoading(true);
    const file = e.target.files[0];
    const upload = await UploadCloudinary(file);
    setImage(upload.secure_url);
    setAlertMessage({
        type: 'success',
        message: 'Image uploaded successfully!'
        });
    setUploadLoading(false);
}

const chooseFile = useCallback(() => {
  const dropArea = document.querySelector(".drop_box");
  const button = dropArea.querySelector("button");
  const input = dropArea.querySelector("input");
  button.onclick();
  input.click();
}, [])

  return (
    <div className="container" style={{marginTop: '40px'}}>

      <div className='col-6 mx-auto card card-body'>
      <h5 className='text-center'>Purple Signature Generator</h5>
      <div class="form-group">
        <label for="exampleInputEmail1">Name:</label>
        <input type="email" name='email' class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Mr. Kayode"/>
      </div>

      <div class="form-group">
        <label for="exampleInputEmail1">Job Designation:</label>
        <input type="email" name='email' class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="Senior Advocate"/>
      </div>

      <div class="form-group">
        <label for="exampleInputEmail1">Official Email address:</label>
        <input type="email" name='email' class="form-control" id="exampleInputEmail1" aria-describedby="emailHelp" placeholder="email@purple.xyz"/>
      </div>
      <div class="form-group">
        <label for="exampleInputPassword1">Phone Number 1:</label>
        <input type="text" name='phoneNumber1' class="form-control" id="exampleInputPassword1" placeholder="+234 0000001"/>
      </div>

      <div class="form-group">
        <label for="exampleInputPassword1">Phone Number 2:</label>
        <input type="text" name='phoneNumber2' class="form-control" id="exampleInputPassword1" placeholder="+234 0000002"/>
      </div>

      <br />

      <br />
      <span>Photo:</span>
      {uploadLoading && <p className="text-success">Loading...</p>}
      {image ? <img width="10%" class="img-profile" src={image} alt="" /> : <img class="img-profile" src={formState.picture} width="10%" alt="" />
      }
      <div className="drop_box">
          <label htmlFor="fileID" className="btn btn-sm btn-primary">Upload Photo</label>
          <input name="file" type="file" onChange={handleFile} hidden accept='png, jpg' id="fileID" style={{ display: 'none' }} />
          <button onClick={chooseFile} disabled={uploadLoading && true} className="btn-sm btn-upload"><i className="fa fa-cloud-upload fa-3x" aria-hidden="true"></i></button>
      </div>

      <button type="submit" class="btn btn-primary">Submit</button>

    </div>
    </div>
  );
}

export default App;
