import './App.css';
import { useCallback, useState } from 'react';
import UploadCloudinary from './cloudinary/UploadCloudinary';
import createUser from './fauna/createUser';


function App() {
  const [uploadLoading, setUploadLoading] = useState(false);
  const [image, setImage] = useState(null);
  const [generatedSignature, setGeneratedSignature] = useState(false);
  const [alertMessage, setAlertMessage] = useState({
    type: '',
    message: ''
  });
  const [formState, updateFormState] = useState({
    email: '',
    phoneNumber1: '',
    phoneNumber2: '',
    name: '',
    jobDesignation: '',
    department: '',

  });

  const handleFile = async e => {
    setUploadLoading(true);
    const file = e.target.files[0];

    if (!file.type.match('image.*')) {
      setAlertMessage({
        type: 'danger',
        message: 'Please upload an image file'
      });
      setUploadLoading(false);
      return;
    }
    if (file.size > 1000000) {
      setAlertMessage({
        type: 'danger',
        message: 'Image size should not exceed 1MB'
      });
      setUploadLoading(false);
      return;
    }
    const upload = await UploadCloudinary(file);
    setImage(upload.secure_url);
    setAlertMessage({
      type: 'success',
      message: 'Image uploaded successfully!'
    });
    setUploadLoading(false);
  }

  const chooseFile = useCallback(() => {
    const dropArea = document.querySelector(".drop_box");
    const button = dropArea.querySelector("button");
    const input = dropArea.querySelector("input");
    button.onclick();
    input.click();
  }, [])



  const updateForm = e => {
    const { value, name } = e.target;
    updateFormState({
      ...formState,
      [name]: value
    });
  };

  const generateSignature = async () => {

    if (!formState.name || !formState.jobDesignation || !formState.email || !formState.department || !formState.phoneNumber1) {
      setAlertMessage({
        type: 'danger',
        message: 'Please fill all fields'
      });
      return;
    }

    const adminEmail = formState.email;
    const adminEmailDomain = adminEmail.split('@')[1];
    // if (adminEmailDomain !== 'purple.xyz') {
    //   setAlertMessage({
    //     type: 'danger',
    //     message: 'Please enter an official email address'
    //   });
    //   return;
    // }

    setGeneratedSignature(true);

    const data = {
      ...formState,
      image
    }

    const createdProfile = await createUser(data);

    if (createdProfile === 'userExists') {
      alert('User with email already exists');
      return;
    }
  }

  const generateHtml = () => {
    highlightSignature();
    setAlertMessage({
      type: 'success',
      message: 'Signature copied to clipboard'
    });
  }

  setTimeout(() => {
    clearAlertMessage();
  }, 20000);

  const clearAlertMessage = () => {
    setAlertMessage({
      type: '',
      message: ''
    });
  }


  // highlight the signature preview
  const highlightSignature = () => {
    const signature = document.getElementById('signature');
    const range = document.createRange();
    range.selectNode(signature);

    const selection = window.getSelection();
    selection.removeAllRanges();
    selection.addRange(range);

    document.execCommand('copy');
  }

  return (
    <div class="container" style={{marginTop: '100px'}}>


      <h1 class="well">Registration Form</h1>

      <div class="col-lg-12 well card">
        <div class="row card-body">

          <input type="hidden" name="reference" value="372789" />

          <div class="col-sm-12">
            <div class="row">
              <div class="col-sm-4 form-group">
                <label>First Name</label>
                <input type="text" name="fname" required="required" placeholder="Enter First Name Here.." class="form-control" />
              </div>
              <div class="col-sm-4 form-group">
                <label>Middle Name</label>
                <input type="text" name="mname" placeholder="Enter Middle Name Here.." class="form-control" />
              </div>
              <div class="col-sm-4 form-group">
                <label>Last Name</label>
                <input type="text" name="lname" required="required" placeholder="Enter Last Name Here.." class="form-control" />
              </div>
            </div>

            <div class="row">
              <div class="col-sm-6 form-group">
                <label>Gender</label>
                <select class="form-control" name="gender" required="required" >
                  <option value=""></option>
                  <option> Male </option>
                  <option> Female </option>
                </select>
              </div>
              <div class="col-sm-6 form-group">
                <label>Date of Birth</label>
                <input type="text" name="dob" placeholder="Enter Date of Birth Here.." class="form-control" required />
              </div>
            </div>

            <div class="row">
              <div class="col-sm-6 form-group">
                <label>Email Address</label>
                <input type="email" name="email" placeholder="Enter Email Address Here.." class="form-control" required />
              </div>
              <div class="col-sm-6 form-group">
                <label>Whatsapp Number </label>
                <input type="Number" name="phone" placeholder="Enter Whatsapp number Here.." class="form-control" required />
              </div>
            </div>

            <div class="form-group">
              <label>Contact Address</label>
              <textarea placeholder="Enter Contact Address Here.." rows="3" class="form-control" name="address" required></textarea>
            </div>


            <h3 class=" ">Bank Details</h3>

            <div class="form-group">
              <label>Bank Name</label>
              <input type="text" placeholder="Enter Bank Name Here.." class="form-control" name="bank_name" required />
            </div>
            <div class="form-group">
              <label>Account Name</label>
              <input type="text" placeholder="Enter Account Name Here.." class="form-control" name="acount_name" required />
            </div>
            <div class="form-group">
              <label>Account Number</label>
              <input type="text" placeholder="Enter Account Number Here.." class="form-control" name="acount_number" required />
            </div>
            <button type="Submit" name="register-form" class="btn btn-lg btn-info">Submit</button>




          </div>
          <p>&nbsp;</p>
          <p style={{margin: "0px 15px 10px"}}>Kindly confirm that the phone number you provided is a whatsapp number. You will be added to a whatsapp group with one week of your registration.</p>


        </div>
      </div>

    </div>
  );
}

export default App;
